// 1. Get and Clean the Content
const rawContent = $input.first().json.choices?.[0]?.message?.content || $input.first().json.content || "";

// AGGRESSIVE CLEANING & REPAIR
let cleanJson = rawContent.replace(/```json/g, '').replace(/```/g, '').trim();
const firstBrace = cleanJson.indexOf('{');
if (firstBrace !== -1) {
    cleanJson = cleanJson.substring(firstBrace);
    if (!cleanJson.trim().endsWith('}')) cleanJson += '}'; 
}

// 2. Parse
let data = {};
try {
    data = JSON.parse(cleanJson);
} catch (e) {
    try {
        cleanJson = cleanJson.replace(/,\s*}$/, '}');
        data = JSON.parse(cleanJson);
    } catch (err2) { data = {}; }
}

// 3. MAP THE FIELDS (UPDATED TO NEW KEYS)
let finalName = data["Candidate Name"] || data["Candidate_Name"] || data["Name"] || "Unknown";
let finalEmail = data["Email"] || "Unknown";
let finalPhone = data["Phone"] || "Unknown";
let finalLocation = data["Lived at"] || "Unknown";
let finalSkills = data["Skills"] || "N/A";
let finalScore = parseInt(data["Score"]) || 0;
let finalRec = data["Recommendation"] || "Pending";
let finalReason = data["Reasoning"] || "See Resume";

// --- LOGIC 1: THE "INCOMPLETE" CHECK (UPDATED) ---
// Now it only rejects if the core profile (Name or Skills) is completely missing.
if (finalName === "Unknown" || finalSkills === "N/A") {
    finalRec = "Not Hired";
    finalScore = 0;
    finalReason = "AUTO-REJECTION: Application incomplete or parsing failed.";
}

// --- LOGIC 2: THE "SCORE ENFORCER" ---
// 1. RED ZONE: Hard Reject for very low scores (< 60)
if (finalScore < 60) {
    finalRec = "Not Hired";
} 
// 2. YELLOW ZONE: The "Breather" for decent candidates (60 - 74)
else if (finalScore >= 60 && finalScore < 75) {
    finalRec = "Waitlist"; 
}
// 3. GREEN ZONE: If 75+, we respect the AI's "Interview" or "Hire" label.

// -----------------------------------------

return [{
    json: {
        "Candidate Name": finalName,
        "Email": finalEmail,
        "Phone": "'" + finalPhone,
        "Lived at": finalLocation,
        "Summary": data["Summary"] || "N/A",
        "Work Experience": data["Work Experience"] || "N/A",
        "Skills": finalSkills,
        "Score": finalScore,
        "Recommendation": finalRec,
        "Reasoning": finalReason
    }
}];

